var EventEmitter = require('events').EventEmitter;
var addon = require('./node-bt-pan-mw.node');

addon.SBluetoothPan.prototype.__proto__ = EventEmitter.prototype;

/**
 * Enum for Bluetooth PAN roles.
 * @readonly
 * @enum {number}
 */
addon.SBluetoothPan.prototype.S_BT_PAN_ROLE = {
    /** None **/
    S_BT_PAN_ROLE_NONE: 0,
    /** PAN User **/
    S_BT_PAN_ROLE_PANU: 16,
    /** Group Ad-hoc Network **/
    S_BT_PAN_ROLE_GN: 32,
    /** Network Access Point **/
    S_BT_PAN_ROLE_NAP: 64
};


/**
 * Enum for Bluetooth PAN status.
 * @readonly
 * @enum {number}
 */
addon.SBluetoothPan.prototype.S_BT_PAN_STATUS = {
    /** None **/
    S_BT_PAN_STATUS_NONE: 0,
    /** Connected **/
    S_BT_PAN_STATUS_CONNECTED: 16
};

module.exports = addon;