'use strict';


var autoBtPanConnector = null;


exports.getInstance = function () {
    if (autoBtPanConnector === null) {
        if (process.platform === 'win32') {
            var AutoBtPanConnector = require('./win/autoBtPanConnector').AutoBtPanConnector;
            autoBtPanConnector = new AutoBtPanConnector();
        } else {
            var AutoBtPanConnector = require('./mac/autoBtPanConnector').AutoBtPanConnector;
            autoBtPanConnector = new AutoBtPanConnector();
        }
    }

    return autoBtPanConnector;
};
