/*
  error_manager.js

  description:
  error data converter from mw-pan|pairing to altair

  @require define.js
*/
'use strict';

const MWError = require('mw-error');

var ErrorManager = {
    /**
     * @function convertForAltair
     * @memberof ErrorManager
     * @return {object} object passed to this function
     */
    convertForAltair: function(errorInfo) {
        // 空文字やNullなどは undefined で統一する
        if (!errorInfo && errorInfo !== 0) {
            return undefined;
        }

        return MWError.genError(MWError.E_MW_AUTO_BT_PAN_CONNECTION_FAILED, errorInfo.toString());
    }
};

module.exports = ErrorManager;
