const expect = require('chai').expect;
const ErrorManager = require('../lib/error_manager');


describe('ErrorManager', function() {
    describe('#convertForAltair(error)', () => {
        var error = {
            error_code: 1234,
            message: "test error object"
        };
        it ('should return object passed to this function', () => {
            var ret = ErrorManager.convertForAltair(error);
            expect(ret.error_code).eq(error.error_code);
            expect(ret.message).eq(error.message);
        });
        it ('should return undefined if empty or null value', () => {
            expect(ErrorManager.convertForAltair(null)).to.undefined;
            expect(ErrorManager.convertForAltair("")).to.undefined;
        });
        it ('should return it if zero value is passed', () => {
            expect(ErrorManager.convertForAltair("0000")).eq("0000");
            expect(ErrorManager.convertForAltair(0)).eq(0);
        });
    });
});