'use strict';


const comparator = exports;

comparator.equal = function (left, right) {
  return (left === right);
};

comparator.notEqual = function (left, right) {
  return  (left !== right);
};

comparator.lessThan = function (left, right) {
  return (left < right);
};

comparator.lessThanEqual = function (left, right) {
  return (left <= right);
};

comparator.greaterThan = function (left, right) {
  return (left > right);
};

comparator.greaterThanEqual = function (left, right) {
  return (left >= right);
};
