
process.env.DEBUG = '*:error';

var autoMagicClient = require('../index.js');

process.env.MW_DEBUG_AUTO_MAGIC = true;

const appLanguage = 'Japanese';
const deviceVersion = '1.0.00.0000';
const deviceModel = 'DPT-RP1';
const deviceSkuCode = 'J';


autoMagicClient.checkForDeviceUpdate(
  deviceVersion,
  deviceModel,
  deviceSkuCode,
  appLanguage,
  function(error, updateInfo){
  if (error) {
    console.error('Update Check Failed: ', error);
    return;
  }
  console.log((updateInfo) ? 'Update Found : ' + updateInfo.newVersion : 'Update Not Found');
  console.log(updateInfo);
});

