
process.env.DEBUG = "*:mw-discovery-ctrl:*";

var ctrl = require('../discoveryctrl.js');

ctrl.on('deviceAppear', function(device){
  console.log('\n<deviceAppear>\n', device);
  console.log(ctrl.getDeviceList());
});

ctrl.on('deviceDisappear', function(device){
  console.log('\n<deviceDisappear>\n', device);
  console.log(ctrl.getDeviceList());
});

ctrl.on('usbSerialConnected', function(device){
  console.log('\n<usbSerialConnected> \n', device);
});

ctrl.on('usbSerialDisconnected', function(device){
  console.log('\n<usbSerialDisconnected>\n', device);
});

ctrl.on('usbEtherConnected', function(device){
  console.log('\n<usbEtherConnected> \n', device);
});

ctrl.on('usbEtherDisconnected', function(device){
  console.log('\n<usbEtherDisconnected> \n', device);
});


