
function createServer() {
  var toIpAddr = '192.168.46.130';
  var toPort = 80;
  var httpForwarder = net.createServer(function(from) {
	var to = net.createConnection({
	  host: toIpAddr,
	  port: toPort});
	from.pipe(to);
	to.pipe(from);
  });
  return httpForwarder;
}

var lastErr = null;
// httpForwarder.listen(58080, 'localhost');
function checkCallback(err) {
  if ( err ) {
	lastErr = err;
	console.error('RESULT is err: ', err);
	return;
  }
  console.log('SUCCEED');
}

