
process.env.DEBUG = '*';

const fs = require('fs');
const regStore = require('../index.js');


fs.readFile('deneb.crt', function (error, data) {
  if (error) {
    console.error(error);
    return;
  }
  regStore.addServerCert(data.toString(), function (error) {
    if (error) {
      console.error(error);
      return;
    }
    console.log('regStore.addServerCert() succeeded.');
  });
});



