#pragma once

#include "CriticalSection.h"

class AutoCriticalSection
{
private:
	CriticalSection* m_pCs;
public:
	AutoCriticalSection(CriticalSection* pCs) : m_pCs(pCs) {
		if (this->m_pCs) {
			this->m_pCs->Lock();
		}
	}
	~AutoCriticalSection() {
		if (this->m_pCs) {
			this->m_pCs->Unlock();
		}
	}
};

