#pragma once

#include <windows.h>

class CriticalSection
{
private:
	CRITICAL_SECTION m_cs;

public:
	CriticalSection() {
		::InitializeCriticalSection(&this->m_cs);
	}
	~CriticalSection() {
		::DeleteCriticalSection(&this->m_cs);
	}

	void Lock() {
		::EnterCriticalSection(&this->m_cs);
	}

	void Unlock() {
		::LeaveCriticalSection(&this->m_cs);
	}
};

