#pragma once

#include <iostream>
#include <sstream>
#include <unistd.h>
#include <string>

class MacErrorCode
{
public:
	static std::string ToString(/*[in]*/ kern_return_t error)
	{
		return "MAC:" + std::to_string(error) + ":kern_return_t error code (@see IOReturn.h)";
	}
};

