#pragma once

#define VERIFY(_condition, _exception, _message) if (!(_condition)) { (_message); throw (_exception); }
#define VERIFY_WARN(_condition, _message) if (!(_condition)) { (_message); }
#define VERIFY_BREAK(_condition, _message) if (!(_condition)) { (_message); break; }
#define VERIFY_CONTINUE(_condition, _message) if (!(_condition)) { (_message); continue; }
#define THROW(_exception, _message) { (_message); throw (_exception); }
