#pragma once

#include <string>
#include <sstream>


#ifdef _MSC_VER
// Windows
#include <tchar.h>
#define tprintf_s   _tprintf_s

#ifdef _UNICODE
#define WIN_UNICODE
typedef wchar_t tchar;
typedef std::wstring tstring;
typedef std::wstringstream tstringstream;
#else
typedef char tchar;
typedef std::string tstring;
typedef std::stringstream tstringstream;
#endif

#else
// Mac OS X
#define _T(x) x
#define tprintf_s   printf
typedef char tchar;
typedef std::string tstring;
typedef std::stringstream tstringstream;

#endif
